        return 'Pessoal';
      case 'PROFISSIONAL':
      case 'PROFISSIONAL_MENSAL':
      case 'PROFISSIONAL_VITALICIO':
        return 'Profissional';
      case 'EMPRESARIAL':
        return 'Empresarial';
      default:
        return 'Pessoal';
    }
  }
  isValidEmail(email) {
    if (!email) return false;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }
  isValidNome(nome) {
    return Boolean(nome && nome.trim().length >= 2);
