    // Verifica se há espaços no início ou fim
    if (email.trim() !== email) {
      return {
        invalidFormat: true
      };
    }
    // Verifica se contém múltiplos emails (separados por vírgula, ponto e vírgula, etc.)
    if (email.includes(',') || email.includes(';') || email.includes('|')) {
      return {
        multipleEmails: true
      };
