    // Ajusta a página atual se necessário
    if (this.totalPaginasCampos === 0) {
      this.paginaCampos = 0;
    } else if (this.paginaCampos >= this.totalPaginasCampos) {
      this.paginaCampos = this.totalPaginasCampos - 1;
